mapper.insert:
insert into user (id, name, age, password, sex, province, address, city, description, my_datetime, my_date)
values (#{user.id}, #{user.name}, #{user.age},
json_array(

    #{password}
), #{user.sex}, #{user.province},
#{user.address}, #{user.city}, #{user.description}, #{user.datetime}, #{user.date})

mapper.select:
select * from user
where my_date >= #{requestBody.startDate} and date(my_datetime)>= #{requestBody.startDate}
and my_date<=#{requestBody.endDate}

    and id >= #{requestBody.id}


    and age <= #{requestBody.age}



        and name = #{requestBody.name}


        and sex = #{requestBody.sex}


limit 10 offset 0

select ${groupField} as field, count(*) as count
from user
group by ${groupField}

select * from user


        and id >= #{requestBody.id}


        and age <= #{requestBody.age}


        and name rlike #{requestBody.name}
